// src/components/Analytics.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Line, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const Analytics = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAnalytics();
  }, []);

  const fetchAnalytics = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/analytics/stats', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching analytics:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Loading analytics...</div>;
  if (!stats) return <div>No data available</div>;

  const barData = {
    labels: stats.notesByDay.map(day => `${day._id.month}/${day._id.day}`),
    datasets: [
      {
        label: 'Notes Created',
        data: stats.notesByDay.map(day => day.count),
        backgroundColor: 'rgba(45, 45, 45, 0.8)',
      },
    ],
  };

  const lineData = {
    labels: ['Total Notes', 'Recent Notes (7 days)'],
    datasets: [
      {
        label: 'Note Statistics',
        data: [stats.totalNotes, stats.recentNotes],
        borderColor: 'rgb(45, 45, 45)',
        backgroundColor: 'rgba(45, 45, 45, 0.1)',
      },
    ],
  };

  return (
    <div>
      <div className="page-header">
        <h1>Analytics Dashboard</h1>
      </div>

      <div className="security-cards">
        <div className="security-card">
          <h3>ðŸ“Š Total Notes</h3>
          <p style={{ fontSize: '2rem', fontWeight: 'bold' }}>{stats.totalNotes}</p>
        </div>
        
        <div className="security-card">
          <h3>ðŸ“ˆ Recent Activity</h3>
          <p style={{ fontSize: '2rem', fontWeight: 'bold' }}>{stats.recentNotes}</p>
          <p>notes created in last 7 days</p>
        </div>
        
        <div className="security-card">
          <h3>ðŸ“… Average per Week</h3>
          <p style={{ fontSize: '2rem', fontWeight: 'bold' }}>{stats.averageNotesPerWeek}</p>
        </div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginTop: '30px' }}>
        <div className="security-card">
          <h3>Daily Notes Trend</h3>
          <Bar data={barData} />
        </div>
        
        <div className="security-card">
          <h3>Notes Overview</h3>
          <Line data={lineData} />
        </div>
      </div>
    </div>
  );
};

export default Analytics;
