import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ onLogin }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: ''
  });
  const [passwordStrength, setPasswordStrength] = useState(0);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Test backend connection first
  const testBackendConnection = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/test');
      return response.data;
    } catch (error) {
      throw new Error('Backend server is not running. Please make sure the backend is started on port 5000.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // First test backend connection
      await testBackendConnection();
      
      const endpoint = isLogin ? '/api/auth/login' : '/api/auth/register';
      const response = await axios.post(`http://localhost:5000${endpoint}`, {
        name: formData.name,
        email: formData.email,
        password: formData.password
      });
      
      if (response.data.token) {
        localStorage.setItem('token', response.data.token);
        onLogin(response.data.user);
      }
    } catch (error) {
      console.error('Login error:', error);
      
      if (error.response) {
        // Server responded with error status
        setError(error.response.data.message || 'An error occurred during authentication.');
      } else if (error.request) {
        // Request was made but no response received
        setError('Cannot connect to the server. Please make sure the backend is running on port 5000.');
      } else {
        // Something else happened
        setError('An unexpected error occurred: ' + error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    if (name === 'password') {
      calculatePasswordStrength(value);
    }
  };

  const calculatePasswordStrength = (password) => {
    let strength = 0;
    if (password.length >= 8) strength += 25;
    if (/[A-Z]/.test(password)) strength += 25;
    if (/[0-9]/.test(password)) strength += 25;
    if (/[^A-Za-z0-9]/.test(password)) strength += 25;
    setPasswordStrength(strength);
  };

  // Mock login for demonstration (if backend fails)
  const handleMockLogin = () => {
    setError('Using demo mode (backend not connected). For full functionality, please ensure the backend server is running.');
    
    // Mock user data for demonstration
    const mockUser = {
      id: 'demo-user-123',
      name: formData.name || 'Demo User',
      email: formData.email || 'demo@example.com'
    };
    
    localStorage.setItem('token', 'demo-token-123');
    onLogin(mockUser);
  };

  return (
    <div className="login-container">
      <div className="login-left">
        <h1>Your Private Thoughts, Secured</h1>
        <p>CipherSoul uses military-grade encryption to keep your journal entries private. Only you can access your thoughts and memories.</p>
        
        <div style={{background: 'rgba(255,255,255,0.1)', padding: '15px', borderRadius: '5px', marginTop: '20px'}}>
          <h4>üîß Backend Status</h4>
          <button 
            type="button" 
            onClick={testBackendConnection}
            style={{padding: '5px 10px', margin: '5px 0', background: 'transparent', border: '1px solid white', color: 'white'}}
          >
            Test Backend Connection
          </button>
          <p style={{fontSize: '0.9rem', marginTop: '10px'}}>Ensure backend is running on port 5000</p>
        </div>
      </div>
      
      <div className="login-right">
        <form className="login-form" onSubmit={handleSubmit}>
          <h2>{isLogin ? 'Welcome back' : 'Create your account'}</h2>
          <p>{isLogin ? 'Sign in to your secure journal' : 'Join us to start your encrypted journal'}</p>
          
          {error && (
            <div style={{
              padding: '15px',
              backgroundColor: '#fee',
              border: '1px solid #fcc',
              borderRadius: '6px',
              marginBottom: '20px',
              color: '#c33',
              fontSize: '0.9rem'
            }}>
              <strong>Error:</strong> {error}
              <div style={{marginTop: '10px'}}>
                <button 
                  type="button" 
                  onClick={handleMockLogin}
                  style={{
                    padding: '8px 15px',
                    background: '#2d2d2d',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  Continue in Demo Mode
                </button>
              </div>
            </div>
          )}

          {!isLogin && (
            <div className="form-group">
              <label htmlFor="name">Full Name</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
          )}
          
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            {!isLogin && (
              <div className="password-strength">
                <div 
                  className="strength-meter" 
                  style={{
                    width: `${passwordStrength}%`,
                    backgroundColor: passwordStrength < 50 ? '#e74c3c' : passwordStrength < 75 ? '#f39c12' : '#2ecc71'
                  }}
                ></div>
              </div>
            )}
          </div>
          
          <button 
            type="submit" 
            className="btn btn-primary" 
            style={{width: '100%'}}
            disabled={loading}
          >
            {loading ? 'üîê Processing...' : (isLogin ? 'Sign In' : 'Create Account')}
          </button>

          <div style={{textAlign: 'center', margin: '15px 0'}}>
            <span style={{color: '#666'}}>or</span>
          </div>

          <button 
            type="button"
            onClick={handleMockLogin}
            className="btn btn-outline" 
            style={{width: '100%'}}
          >
            üöÄ Try Demo Mode
          </button>
          
          <p style={{textAlign: 'center', marginTop: '24px'}}>
            {isLogin ? "Don't have an account? " : "Already have an account? "}
            <button 
              type="button" 
              className="btn btn-link" 
              onClick={() => {
                setIsLogin(!isLogin);
                setError('');
              }}
            >
              {isLogin ? 'Sign up' : 'Sign in'}
            </button>
          </p>
        </form>
      </div>
    </div>
  );
};

export default Login;
